window.PORTAL_UI.dropdownTrigger=function(o){var n=window.PORTAL_UI.usingAngular?$(o).closest(".dropdown-trigger"):$(this),d=n.next(".dropdown");$(".dropdown").not(d).removeClass("dropdown-open"),d.toggleClass("dropdown-open")},window.PORTAL_UI.hideAllDropdowns=function(){$(".dropdown").removeClass("dropdown-open")},window.PORTAL_UI.usingAngular||$(document).ready(function(){$(".dropdown-trigger").click(window.PORTAL_UI.dropdownTrigger)});